INSERT INTO gbua_cgdb.PagePrimaryTitle (PageID, LangID, PrimaryTitle, PrimaryTitleDescryption, page_templete_title, page_templete_description, page_templete_keywords) VALUES (30, 4, 'Весілля в Грузії. Кахетія.', 'Місто закоханих - Сигнахі. ЗАГС тут працює цілодобово. Вас чекає край кохання, виноградні лози та Алазанська долина!', 'Весілля в Грузії. Кахетія.', 'Свадьба в Кахетии недорого. Всё включено! Роспись в городе влюблённых - Сигнахи. Вас ждет край любви, виноградники и алазанская долина!', 'свадьба в грузии, свадьба в сигнахи, свадьба в кахетии, венчание в грузии');
INSERT INTO gbua_cgdb.PagePrimaryTitle (PageID, LangID, PrimaryTitle, PrimaryTitleDescryption, page_templete_title, page_templete_description, page_templete_keywords) VALUES (31, 4, 'Весілля в Грузії. Кахетія. Кварелі', 'Мальовничі краєвиди на виноградники і гори Кахетії - ідеальне місце для весілля! Виїзна церемонія розпису, затишний готель, насичена і захоплююча програма для розваги гостей і молодят на території винного заводу, національне грузинське застілля і веселий тамада!', 'Весілля в Грузії. Кахетія. Виїзна церемонія реєстрації шлюбу', 'Весілля в Кахетії, все включено! Проживання в готелі серед виноградників, розпис і ресторан на території готелю, програма развленій для гостей. Розраховано на 10 осіб', 'весілля в грузії з виїзною церемонією, весілля в Кахетії серед виноградників, весілля в Кахетії все включено');
